import * as i2 from "src/app/services/project.service";
export class ProjectcompleteComponent {
    constructor(route, router, projectService) {
        this.route = route;
        this.router = router;
        this.projectService = projectService;
        this.project = null;
        this.projectId = null;
        this.isProjectsLoaded = false;
    }
    ngOnInit() {
        this.getProjectIdFromURL();
        this.loadProject();
    }
    getProjectIdFromURL() {
        this.route.queryParams
            .subscribe((params) => {
            this.projectId = params.id;
        });
        if (this.projectId == null)
            this.router.navigate(['/projectlist']);
    }
    async loadProject() {
        await this.projectService.getProjectById(this.projectId).subscribe({
            next: (resp) => {
                this.project = resp;
                this.isProjectsLoaded = true;
            },
            error: (rej) => {
                alert("Something went wrong...");
            }
        });
    }
    savePDF() {
        let DATA = document.getElementById('content');
        html2canvas(DATA).then((canvas) => {
            let fileWidth = 208;
            let fileHeight = (canvas.height * fileWidth) / canvas.width;
            const FILEURI = canvas.toDataURL('image/png');
            let PDF = new jsPDF('p', 'mm', 'a4');
            let position = 0;
            PDF.addImage(FILEURI, 'PNG', 0, position, fileWidth, fileHeight);
            PDF.save('NFTINFO.pdf');
        });
    }
}
