import { FormControl, FormGroup, Validators } from '@angular/forms';
import * as i0 from "@angular/core";
import * as i1 from "src/app/services/user.service";
import * as i2 from "ngx-cookie-service";
import * as i3 from "@angular/router";
import * as i4 from "@angular/forms";
export class LoginComponent {
    constructor(userService, cookies, router) {
        this.userService = userService;
        this.cookies = cookies;
        this.router = router;
        this.passwordRegex = "^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,}$";
        this.form = new FormGroup({
            username: new FormControl('', Validators.required),
            password: new FormControl('', [Validators.required, Validators.pattern(this.passwordRegex)])
        });
    }
    ngOnInit() {
    }
    onSubmit() {
        if (this.form.invalid) {
            this.showAlert();
            return;
        }
        this.login();
    }
    async login() {
        await this.userService.login(this.form.value).subscribe({
            next: (resp) => {
                console.log(resp);
                this.cookies.set("nftmtpauth", resp.Auth);
                alert("User loging succesfully");
                //Redirect to somewhere
            },
            error: (rej) => {
                console.log(rej);
                alert("Something goes wrong...");
            }
        });
    }
    showAlert() {
        if (this.form.controls["username"].status == "INVALID") {
            alert("Username is invalid");
            return;
        }
        if (this.form.controls["password"].status == "INVALID") {
            alert("Password is invalid");
            return;
        }
    }
}
